mapper.select:
SELECT  noticeno, memberno, title, content, word, rdate, cnt
FROM notice
WHERE noticeno=#{noticeno}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}
WHERE noticeno = #{noticeno}

update notice 
set cnt = cnt + 1 
where noticeno = #{noticeno}

UPDATE notice
SET replycnt = replycnt + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET replycnt = replycnt - 1
WHERE noticeno = #{noticeno}
